

using Api.Core.Logica;

namespace Api.TestsUnitarios
{
	public class ImagenUtilityTest
	{		
		// public static string PuntoRojoBase64 = "iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg==";
		private const string PuntoRojoBase64ConUriDataJpg = "data:image/jpeg;base64, iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg==";
		private const string RectanguloVerticalBase64 = "";

		[Fact]
		public void ConvertirABitMapYATamanio240X240()
		{
			var bitMap = ImagenUtility.ConvertirABitMapYATamanio240X240(PuntoRojoBase64ConUriDataJpg);
			Assert.Equal(240, bitMap.Width);
			Assert.Equal(240, bitMap.Height);
		}

		[Fact]
		public void ImagenCuadradaRotarAHorizontalYComprimir()
		{
			var bytes = Convert.FromBase64String(RectanguloVerticalBase64);
			var stream = new MemoryStream(bytes);

			var image = ImagenUtility.RotarAHorizontalYComprimir(stream);

			Assert.True(image.Width > image.Height);
		}

	}
}
